mapper.insert:
INSERT INTO lc_survey(
			s_title,
			s_description,
			s_writer
			)
VALUES (
		#{sTitle},
		#{sDescription},
		#{sWriter}
		)

INSERT INTO lc_question(
			s_idx,
			t,
			q
			)
VALUES (
		select s_idx FROM lc_survey ORDER BY 	 s_idx DESC  LIMIT 1),
		#{t},
		#{q}
		)

INSERT INTO vue_response(
			sub_num,
			q_num,
			t
) VALUES (
		(select sub_num FROM vue_submission ORDER BY 	 sub_num DESC  LIMIT 1),
		#{q_num},
		#{t}
		)
